'use strict';

var setQueryString = function setQueryString(query) {
  var queryString = "";
  console.log(query);
  if (typeof query !== 'undefined') {
    if (typeof query._with !== 'undefined') {
      queryString += "with=";
      query._with.forEach(function (w) {
        queryString += w + ',';
      });
    }

    if (typeof query.fields !== 'undefined') {
      queryString += "&fields=";
      query.fields.forEach(function (field) {
        queryString += field + ',';
      });
    }

    if (typeof query.filter !== 'undefined') {
      queryString += "&";
      query.filter.forEach(function (filter) {
        queryString += filter + '&';
      });
    }
  }
  console.log(queryString);
  return queryString;
};

module.exports = {
  setQueryString: setQueryString
};