'use strict';

var setWith = function setWith(w) {

  var withString = "";
  if (typeof w === 'undefined') {
    w = {};
  }

  if (typeof w.customers !== 'undefined') {
    withString += "customers";
    if (w.customers.length > 0) {
      w.customers.forEach(function (param) {
        withString += '.' + param;
      });
      withString += ",";
    } else {
      withString += ",";
    }
  }

  if (typeof w.user !== 'undefined') {
    withString += "user";
    if (w.user.length > 0) {
      w.user.forEach(function (param) {
        withString += '.' + param;
      });
      withString += ",";
    } else {
      withString += ",";
    }
  }

  return withString;
};

var setFields = function setFields(fields) {
  var fieldsString = "";
  if (typeof fields === 'undefined') {
    fields = [];
  }

  fields.forEach(function (field) {
    fieldsString += field + ',';
  });
  fieldsString = fieldsString.slice(0, -1);

  return fieldsString;
};

module.exports = {
  setWith: setWith,
  setFields: setFields
};