var setWith = (w) => {

  var withString = "";
  if(typeof w === 'undefined')
  {
    w = {};
  }

  if(typeof w.customers !== 'undefined'){
    withString += "customers";
    if(w.customers.length > 0){
      w.customers.forEach(param => {
        withString += `.${param}`;
      });
      withString += ",";
    }
    else{
      withString += ",";
    }
  }

  if(typeof w.user !== 'undefined'){
    withString += "user";
    if(w.user.length > 0){
      w.user.forEach(param => {
        withString += `.${param}`;
      });
      withString += ",";
    }
    else{
      withString += ",";
    }
  }

  return withString;
};


var setFields = (fields) => {
  var fieldsString = "";
  if(typeof fields === 'undefined')
  {
    fields = [];
  }

  fields.forEach(field => {
    fieldsString += `${field},`;
  });
  fieldsString = fieldsString.slice(0,-1);

  return fieldsString;
};

module.exports = {
  setWith,
  setFields
};
